 $(document).ready(function(){

 $("#objectiveButton").on('click',function(){

    var objectivedata=[];
    // loop over each table row (tr)
    $("#objectiveTable tr").each(function(){
         var currentRow=$(this);                                
         var col1_value=currentRow.find(".lineids input").val();
         var col2_value=currentRow.find(".category select").val();
         var col3_value=currentRow.find(".objective input").val();
         var col4_value=currentRow.find(".description input").val();
         var col5_value=currentRow.find(".datefrom input").val();
         var col6_value=currentRow.find(".dateto input").val();
         var col7_value=currentRow.find(".weightage input").val();
         var col8_value=currentRow.find(".priority select").val();
         var col9_value=currentRow.find(".status select").val();

         var obj={};
         obj.col1=col1_value;
         obj.col2=col2_value;
         obj.col3=col3_value;
         obj.col4=col4_value;
         obj.col5=col5_value;
         obj.col6=col6_value;
         obj.col7=col7_value;
         obj.col8=col8_value;

         objectivedata.push(obj);

    });
  objectivedictionary = JSON.stringify(objectivedata); 
  var lista = document.getElementById("objective_vals")
  lista.value = objectivedictionary 
  console.log(objectivedata);   
  });

});

var objective_data_vals = []
function onFormSubmit(){
   var formData = readFormData();
   insertFormRecord(formData);
   onFormLineData(formData);

}
function readFormData(){
  var formData = {};
  formData['categline'] = document.getElementById('categline').value;
  formData['objectiveline'] = document.getElementById('objectiveline').value;
  formData['datefromline'] = document.getElementById('datefromline').value;
  formData['datetoline'] = document.getElementById('datetoline').value;
  formData['weightageline'] = document.getElementById('weightageline').value;
  formData['descriptionline'] = document.getElementById('descriptionline').value;
  formData['priorityline'] = document.getElementById('priorityline').value;
  formData['statusline'] = document.getElementById('statusline').value;
  return formData;
}
function insertFormRecord(data){
   var tableobjective = document.getElementById('objectiverecord');
   var newRow = tableobjective.insertRow(tableobjective.length)
   cell1 = newRow.insertCell(0);
   cell1.innerHTML= data.categline
   cell1.classList.add("categline");
   cell2 = newRow.insertCell(1);
   cell2.innerHTML= data.objectiveline
   cell2.classList.add("objectiveline");
   cell3 = newRow.insertCell(2);
   cell3.innerHTML= data.descriptionline
   cell3.classList.add("descriptionline");
   cell4 = newRow.insertCell(3);
   cell4.innerHTML= data.datefromline
   cell4.classList.add("datefromline");
   cell5 = newRow.insertCell(4);
   cell5.innerHTML= data.datetoline
   cell5.classList.add("datetoline");
   cell6 = newRow.insertCell(5);
   cell6.innerHTML= data.weightageline
   cell6.classList.add("weightageline");                                 
   cell7 = newRow.insertCell(6);
   cell7.innerHTML= data.priorityline
   cell7.classList.add("priorityline");                          
   cell8 = newRow.insertCell(7);
   cell8.innerHTML= data.statusline
   cell8.classList.add("statusline");                          
   cell9 = newRow.insertCell(8);                                   
   cell9.innerHTML= '';

}
function resetForm(){
  document.getElementById('categline').value="";
  document.getElementById('objectiveline').value="";
  document.getElementById('descriptionline').value="";
  document.getElementById('datefromline').value="";   
  document.getElementById('datetoline').value="";   
  document.getElementById('weightageline').value="";   
  document.getElementById('priorityline').value="";  
  document.getElementById('statusline').value="";   
}

function onDelete(td){
      row = td.parentElement.parentElement;
      document.getElementById("objectiverecord").deleteRow(row.rowIndex);
      resetForm();
}

function onFormLineData(data){
   var objectivedata=[];
   var obj={};
   obj.col1=data.project;
   obj.col2=data.task;
   obj.col3=data.description;
   obj.col4=data.duration;
   objectivedata.push(obj);
   timehseetdictionary = JSON.stringify(objectivedata); 
   var lista = document.getElementById("objective_setting_vals")
   lista.append(objectivedata)
}



