form.script:
  $(function () {

    var confirmation_status = $( "#confirmation_status" ).val();
    if ( confirmation_status === "confirm" ) {                                   
       $("#ggross_salary").show();
       $("#ggrade").show();  
       $("#gwith_from").show();
       $("#gprobation_extend_date").hide();                                        
      } 
     if ( confirmation_status === "dont_confirm" ) {                                    
       $("#ggross_salary").hide();
       $("#ggrade").hide();  
       $("#gwith_from").hide(); 
       $("#gprobation_extend_date").hide();                                        
      }
    if ( confirmation_status === "extend" ) {                                    
       $("#ggross_salary").hide();
       $("#ggrade").hide();  
       $("#gwith_from").hide();  
       $("#probation_extend_date").show();                                        
      }

});
  $(document).ready(function(){

  $("#confirmation_status").change(function(){
    var confirmation_status = $( "#confirmation_status" ).val();
    if ( confirmation_status === "confirm" ) {                                   
       $("#ggross_salary").show();
       $("#ggrade").show();  
       $("#gwith_from").show();
       $("#gprobation_extend_date").hide();                                        
      } 
     if ( confirmation_status === "dont_confirm" ) {                                    
       $("#ggross_salary").hide();
       $("#ggrade").hide();  
       $("#gwith_from").hide(); 
       $("#gprobation_extend_date").hide();                                        
      }
    if ( confirmation_status === "extend" ) {                                    
       $("#ggross_salary").hide();
       $("#ggrade").hide();  
       $("#gwith_from").hide();  
       $("#probation_extend_date").show();                                        
      }
  });




});
